package org.example.Practice;
import java.util.*;
public class RollingHash {
    public static final int mod = (int)10e9 + 7;
    public static List<Integer> matcher(String text,String pattern){
        int n = text.length();
        int m = pattern.length();
        long pow = 1,ph = 0,th = 0;
        long base = 256;
        List<Integer> indices = new ArrayList<>();
        for(int i=0;i<m-1;i++){
            pow = (pow * base)%mod;
        }
        for(int i=0;i<m;i++){
            th = ((th * base)%mod + text.charAt(i))%mod;
            ph = ((ph * base)%mod + pattern.charAt(i))%mod;
        }
        for(int i=m;i<=n;i++){
            if(th == ph && text.regionMatches(i-m,pattern,0,m))
                indices.add(i-m);
            if(i<n) {
                th = (((th - (pow * text.charAt(i-m))%mod)*base + mod)%mod + text.charAt(i))%mod;
            }
        }
        return indices;
    }
    public static int isPossible(String text, int k){
        int n = text.length();
        long th = 0,pow = 1,base = 256;
        Map<Long,Integer> index = new HashMap<>();
        for(int i=0;i<k-1;i++){
            pow = (pow * base)%mod;
        }
        for(int i=0;i<k;i++){
            th = ((th * base)%mod + text.charAt(i))%mod;
        }
        index.put(th,0);
        for(int i=k;i<n;i++){
            if(i<n) {
                th = (((th - (pow * text.charAt(i - k)) % mod) * base + mod) % mod + text.charAt(i)) % mod;
            }
            if(index.containsKey(th) && text.regionMatches(i-k+1,text.substring(index.get(th),index.get(th) + k),0,k))
                    return i - k + 1;
            index.put(th,i-k+1);
        }
        return -1;
    }

    public static void main(String[] args) {
        String text = "nirmalsomenirmalnirmalrohan";
        String text1 = "";
        String pattern = "havbc";
        System.out.println(isPossible("banana",5));
    }
}
